const MathWrap = function (value, min, max) {
  const range = max - min;

  return (min + ((((value - min) % range) + range) % range));
};

const Wrap = function (angle) {
  return MathWrap(angle, -Math.PI, Math.PI);
};

const DistanceBetween = function (x1, y1, x2, y2) {
  const dx = x1 - x2;
  const dy = y1 - y2;

  return Math.sqrt(dx * dx + dy * dy);
};

const Phaser = {
  Math: { Angle: {}, Distance: {} },
};
Phaser.Math.Angle.Wrap = Wrap;
Phaser.Math.Distance.Between = DistanceBetween;

module.exports = Phaser;
